import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol){
  // alert("clicked");
  totclicks++;
  nxtSymbol();
  if(totclicks%2 == 0){
    return setSymbol("O")
  }
  return setSymbol("X")
}

function nxtSymbol(){
  if(totclicks%2 == 0) {
    return "X";
  }
  return "O";
}

function Square(){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol)}}>{symbol}</button>;
}

const userContext = createContext();
export default function Board() {
  const [nextSymbol, setNextSymbol] = useState("X");
  return (
    <userContext.Provider value={nextSymbol}>
      <div className="status">Next player: {nxtSymbol()}</div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
    </userContext.Provider>
  )
}
