import React, { useState } from "react";


function Square(prop){
  return (
    <button onClick={prop.onSquareClick} className="square">{prop.index}</button>
  );
}

function Board(){
  const [table, setTable] = useState(Array(9).fill(null));
  const [nxtSymbol, setNextSymbol] = useState(true);

  function handleClick(index){
    if(findWinner(table) || table[index] !== null){
      return;
    }

    const newTable = [...table];
    if(nxtSymbol){
      newTable[index] = "X";
    }
    else{
      newTable[index] = "O";
    }
    setNextSymbol(!nxtSymbol)
    setTable(newTable);    
  }


  const isWinner = findWinner(table);
  let status;
  if(isWinner){
    let sym = nxtSymbol ? "O":"X";
    status = "Winner: " + sym;
  }
  else{
    let sym = nxtSymbol ? "X":"O";
    status = "Next Player: " + sym;
  }

  return (
  <>
    <div className="board-row">
      <p className="status">{status}</p>
      <Square index = {table[0]} onSquareClick = {() => {handleClick(0)}} />
      <Square index = {table[1]} onSquareClick = {() => {handleClick(1)}} />
      <Square index = {table[2]} onSquareClick = {() => {handleClick(2)}} />
    </div>

    <div className="board-row">
      <Square index = {table[3]} onSquareClick = {() => {handleClick(3)}} />
      <Square index = {table[4]} onSquareClick = {() => {handleClick(4)}} />
      <Square index = {table[5]} onSquareClick = {() => {handleClick(5)}} />
    </div>

    <div className="board-row">
      <Square index = {table[6]} onSquareClick = {() => {handleClick(6)}} />
      <Square index = {table[7]} onSquareClick = {() => {handleClick(7)}} />
      <Square index = {table[8]} onSquareClick = {() => {handleClick(8)}} />
    </div>
  </>
  ) 
}

export default function Game(){
  return <Board />
}

function findWinner(newArray){
  for(let i=0; i<3; i++){
    if(newArray[3*i] === newArray[3*i + 1] && newArray[3*i] === newArray[3*i + 2] && newArray[3*i] !== null){
      return true;
    }
    else if(newArray[i] === newArray[i+3] && newArray[i] === newArray[i+6] && newArray[i] !== null){
      return true;
    }
    else if(newArray[0] === newArray[4] && newArray[0] === newArray[8] && newArray[0] !== null){
      return true;
    }
    else if(newArray[2] === newArray[4] && newArray[4] === newArray[6] && newArray[4] !== null){
      return true;
    }
  }
  return false;
}
