import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol){
  // alert("clicked");
  totclicks++;
  nxtSymbol();
  if(totclicks%2 == 0){
    return setSymbol("O")
  }
  return setSymbol("X")
}

function nxtSymbol({setNextSymbol}){
  const nextSymbol = useContext(userContext);
  if(totclicks%2 == 0) {
    return setNextSymbol("X");
  }
  return setNextSymbol("O");
}

function Square(){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol)}}>{symbol}</button>;
}

const userContext = createContext();
export default function Board() {
  const [nextSymbol, setNextSymbol] = useState("X");
  return (
    <userContext.Provider value={nextSymbol}>
      <div className="status">Next player: {() => {nxtSymbol({setNextSymbol})}}</div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
    </userContext.Provider>
  )
}
