import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol, setNextSymbol){
  // alert("clicked");
  totclicks++;
  if(totclicks%2 == 0){
    return setSymbol("O"), setNextSymbol("X");
  }
  return setSymbol("X"), setNextSymbol("O");
}

function nxtSymbol({nextSymbol, setNextSymbol}){
  if(totclicks%2 == 0) {
    setNextSymbol("X");
    return nextSymbol; 
  }
  setNextSymbol("O");
  return nextSymbol;
}

function Square(prop){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol, prop.func)}}>{symbol}</button>;
}

export default function Board() {
  const dict = {
    "11": "Null",
    "12": "Null",
    "13": "Null",
    "21": "Null",
    "22": "Null",
    "23": "Null",
    "31": "Null",
    "32": "Null",
    "33": "Null",
  };
  const [nextSymbol, setNextSymbol] = useState("X");
  return (
    <>
      <div className="status">Next player: {nextSymbol}</div>
      <div className="board-row">
        <Square func = {setNextSymbol}/>
        <Square func = {setNextSymbol}/>
        <Square func = {setNextSymbol}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol}/>
      <Square func = {setNextSymbol}/>
      <Square func = {setNextSymbol}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol}/>
      <Square func = {setNextSymbol}/>
      <Square func = {setNextSymbol}/>
      </div>
    </>
  )
}
