import { useState } from "react";

let totclicks = 0;

function handleClick(num){
  // alert("clicked");
  totclicks++;
  return totclicks%2==0 ? num="O":num="X";
}

function Square({num}){
  return <button className="square" onClick={(num) => {handleClick(num)}}>{num}</button>;
}
export default function Board() {
  return (
    <>
      <div className="board-row">
        <Square num = "x" />
        <Square num = ""/>
        <Square num = ""/>
      </div>
      <div className="board-row">
      <Square num = ""/>
      <Square num = ""/>
      <Square num = ""/>
      </div>
      <div className="board-row">
      <Square num = ""/>
      <Square num = ""/>
      <Square num = ""/>
      </div>
    </>
  )
}
