import { useState } from "react";

let totclicks = 0;

function handleClick(setSymbol){
  // alert("clicked");
  totclicks++;
  if(totclicks%2 == 0){
    return setSymbol("X")
  }
  return setSymbol("O")
}

function Square({num}){
  const [symbol, setSymbol] = useState("X");
  return <button className="square" onClick={() => {handleClick(setSymbol)}}>{symbol}</button>;
}
export default function Board() {
  return (
    <>
      <div className="board-row">
        <Square num = "x" />
        <Square num = ""/>
        <Square num = ""/>
      </div>
      <div className="board-row">
      <Square num = ""/>
      <Square num = ""/>
      <Square num = ""/>
      </div>
      <div className="board-row">
      <Square num = ""/>
      <Square num = ""/>
      <Square num = ""/>
      </div>
    </>
  )
}
