import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol, setNextSymbol, setTable, index){
  // alert("clicked");
  totclicks++;
  if(totclicks%2 == 0){
    return setSymbol("O"), setNextSymbol("X"), setTable( (prev) => {return {...prev, index: "O"}});
  }
  return setSymbol("X"), setNextSymbol("O"), setTable( (prev) => { return {...prev, index: "X"}});
}

function nxtSymbol({nextSymbol, setNextSymbol}){
  if(totclicks%2 == 0) {
    setNextSymbol("X");
    return nextSymbol; 
  }
  setNextSymbol("O");
  return nextSymbol;
}

function Square(prop){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol, prop.func, prop.tabl, prop.index)}}>{symbol}</button>;
}

export default function Board() {
  // const dict = ;
  const [tables, setTable] = useState({
    oneone: "Null",
    onetwo: "Null",
    onethree: "Null",
    twoone: "Null",
    twotwo: "Null",
    twothree: "Null",
    threeone: "Null",
    threetwo: "Null",
    threethree: "Null",
  });
  const [nextSymbol, setNextSymbol] = useState("X");
  return ( 
    <>
      <p>{tables.oneone}</p>
      <div className="status">Next player: {nextSymbol}</div>
      <div className="board-row">
        <Square func = {setNextSymbol} tabl = {setTable} index = {oneone}/>
        <Square func = {setNextSymbol} tabl = {setTable} index = {onetwo}/>
        <Square func = {setNextSymbol} tabl = {setTable} index = {onethree}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} index = {twoone}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {twotwo}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {twothree}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} index = {threeone}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {threetwo}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {threethree}/>
      </div>
    </>
  )
}
