import { createContext, useContext, useState } from "react";

let totclicks = 0;

function winnerFinder(newArray){
  for(let i=0; i<3; i++){
    if(newArray[3*i] == newArray[3*i + 1] && newArray[3*i] == newArray[3*i + 2]){
      return true;
    }
    else if(newArray[i] == newArray[i+3] && newArray[i] == newArray[i+6]){
      return true;
    }
    else if(newArray[0] == newArray[4] && newArray[0] == newArray[8]){
      return true;
    }
    else if(newArray[2] == newArray[4] && newArray[4] == newArray[6]){
      return true;
    }
    return false;
  }
}

function handleClick(setSymbol, setNextSymbol, setTable, tables, index, setWinner){
  // alert("clicked");
  totclicks++;
  const newArray = [...tables];
  if(totclicks%2 == 0){
    newArray[index] = "O";
    let isWinner = winnerFinder(newArray);
    if (isWinner){
      return setSymbol("O"), setNextSymbol("X"), setTable(newArray), setWinner("O");
    }
    else {
      return setSymbol("O"), setNextSymbol("X"), setTable(newArray);
    }
  }
  newArray[index] = "X";
  let isWinner = winnerFinder(newArray);
  if(isWinner){
    return setSymbol("X"), setNextSymbol("O"), setTable(newArray), setWinner("X");
  }
  else {
    return setSymbol("X"), setNextSymbol("O"), setTable(newArray);
  }
}

function nxtSymbol({nextSymbol, setNextSymbol}){
  if(totclicks%2 == 0) {
    setNextSymbol("X");
    return nextSymbol; 
  }
  setNextSymbol("O");
  return nextSymbol;
}

function Square(prop){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol, prop.func, prop.tabl, prop.tables, prop.index, prop.setWinner)}}>{symbol}</button>;
}

export default function Board() {
  const dict = ["", "", "" , "", "", "", "", "", ""];
  const [tables, setTable] = useState(dict);
  const [nextSymbol, setNextSymbol] = useState("X");
  const [winner, setWinner] = useState("");
  return ( 
    <>
      <p>{tables}</p>
      <div className="status">Next player: {nextSymbol}</div>
      <div className="board-row">
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {0} winr  = {setWinner}/>
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {1} winr  = {setWinner}/>
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {2} winr  = {setWinner}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {3} winr  = {setWinner}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {4} winr  = {setWinner}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {5} winr  = {setWinner}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {6} winr  = {setWinner}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {7} winr  = {setWinner}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {8} winr  = {setWinner}/>
      </div>
    </>
  )
}
