import React, { useState } from "react";


function Square(prop){

  const [symbol, setSymbol] = useState("");

  function handleClick(){
    setSymbol("X");
  }

  return (
    <button onClick={prop.onSquareClick} className="square">{prop.index}</button>
  );
}

export default function Board(){
  const [table, setTable] = useState(Array(9).fill(null));

  function handleClick(index){
    const newTable = [...table];
    newTable[index] = "X";
    setTable(newTable);
  }

  return (
  <>
    <div className="board-row">
      <Square index = {table[0]} onSquareClick = {() => {handleClick(0)}} />
      <Square index = {table[1]} onSquareClick = {() => {handleClick(1)}} />
      <Square index = {table[2]} onSquareClick = {() => {handleClick(2)}} />
    </div>

    <div className="board-row">
      <Square index = {table[3]} onSquareClick = {() => {handleClick(3)}} />
      <Square index = {table[4]} onSquareClick = {() => {handleClick(4)}} />
      <Square index = {table[5]} onSquareClick = {() => {handleClick(5)}} />
    </div>

    <div className="board-row">
      <Square index = {table[6]} onSquareClick = {() => {handleClick(6)}} />
      <Square index = {table[7]} onSquareClick = {() => {handleClick(7)}} />
      <Square index = {table[8]} onSquareClick = {() => {handleClick(8)}} />
    </div>
  </>
  ) 
}
