import { useState } from "react";

let totclicks = 0;

function handleClick(setSymbol){
  // alert("clicked");
  totclicks++;
  if(totclicks%2 == 0){
    return setSymbol("O")
  }
  return setSymbol("X")
}

function Square(){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol)}}>{symbol}</button>;
}
export default function Board() {
  return (
    <>
      <div className="status">Next player: {symbol}</div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
      <div className="board-row">
      <Square />
      <Square />
      <Square />
      </div>
    </>
  )
}
