import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol, setNextSymbol, setTable, tables, index){
  // alert("clicked");
  totclicks++;
  const newArray = [...tables];
  if(totclicks%2 == 0){
    newArray[index] = "O";
    return setSymbol("O"), setNextSymbol("X"), setTable(newArray);
  }
  newArray[index] = "X";
  return setSymbol("X"), setNextSymbol("O"), setTable(newArray);
}

function nxtSymbol({nextSymbol, setNextSymbol}){
  if(totclicks%2 == 0) {
    setNextSymbol("X");
    return nextSymbol; 
  }
  setNextSymbol("O");
  return nextSymbol;
}

function Square(prop){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol, prop.func, prop.tabl, prop.tables, prop.index)}}>{symbol}</button>;
}

export default function Board() {
  const dict = ["", "", "" , "", "", "", "", "", ""];
  const [tables, setTable] = useState(dict);
  const [nextSymbol, setNextSymbol] = useState("X");
  return ( 
    <>
      <p>{tables}</p>
      <div className="status">Next player: {nextSymbol}</div>
      <div className="board-row">
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {0}/>
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {1}/>
        <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {2}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {3}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {4}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {5}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {6}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {7}/>
      <Square func = {setNextSymbol} tabl = {setTable} tables = {tables} index = {8}/>
      </div>
    </>
  )
}
