import { createContext, useContext, useState } from "react";

let totclicks = 0;

function handleClick(setSymbol, setNextSymbol, setTable, index){
  // alert("clicked");
  totclicks++;
  if(totclicks%2 == 0){
    return setSymbol("O"), setNextSymbol("X"), setTable( prev => {return {...prev, index: "O"}});
  }
  return setSymbol("X"), setNextSymbol("O"), setTable( prev => { return {...prev, index: "X"}});
}

function nxtSymbol({nextSymbol, setNextSymbol}){
  if(totclicks%2 == 0) {
    setNextSymbol("X");
    return nextSymbol; 
  }
  setNextSymbol("O");
  return nextSymbol;
}

function Square(prop){
  const [symbol, setSymbol] = useState("");
  return <button className="square" onClick={() => {handleClick(setSymbol, prop.func, prop.tabl, prop.index)}}>{symbol}</button>;
}

export default function Board() {
  const dict = {
    11: "Null",
    12: "Null",
    13: "Null",
    21: "Null",
    22: "Null",
    23: "Null",
    31: "Null",
    32: "Null",
    33: "Null",
  };
  const [table, setTable] = useState(dict);
  const [nextSymbol, setNextSymbol] = useState("X");
  return (
    <>
      <div className="status">Next player: {nextSymbol}</div>
      <div className="board-row">
        <Square func = {setNextSymbol} tabl = {setTable} index = {11}/>
        <Square func = {setNextSymbol} tabl = {setTable} index = {12}/>
        <Square func = {setNextSymbol} tabl = {setTable} index = {13}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} index = {21}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {22}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {23}/>
      </div>
      <div className="board-row">
      <Square func = {setNextSymbol} tabl = {setTable} index = {31}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {32}/>
      <Square func = {setNextSymbol} tabl = {setTable} index = {33}/>
      </div>
      console.log({table})
    </>
  )
}
